import { useState } from "react";

const AddExpenseButton = (props) => {
  const [showForm, setShowForm] = useState(false);
  const toggleButton = () => {
    if (showForm === false) {
      setShowForm(true);
    } else {
      setShowForm(false);
    }

    console.log(showForm);
  };


  const form = 
  <div>
  <form onSubmit={props.submitHandler}>
<div className="new-expense__controls">
  <div className="new-expense__control">
    <label>Title</label>
    <input type="text" value={props.newTitle} onChange={props.titleChangeHandler} />
  </div>
  <div className="new-expense__control">
    <label>Amount</label>
    <input
      type="number"
      value={props.newAmount}
      min="0.01"
      step="0.01"
      onChange={props.amountChangeHandler}
    />
  </div>
  <div className="new-expense__control">
    <label>Date</label>
    <input
      type="date"
      value={props.newDate}
      min="2019-01-01"
      max="2022-12-31"
      onChange={props.dateChangeHandler}
    />
  </div>
</div>
<div className="new-expense__actions">

<button style={{
        display: showForm === true ? "inline" : "none"}} 
        onClick={toggleButton}>
            Cancel</button>
  <button type="submit">Add Expense</button>
</div>
</form>
  </div>




  let button = <button onClick={toggleButton}>Add New Expense</button>;

  if (showForm === true) {
    button = form;
  }

  return (
    <div>

      {button}

    </div>
  );
};

export default AddExpenseButton;
