// Date props - App>ExpenseItem>ExpenseDate
import './ExpenseDate.css'

function ExpenseDate(props) {
    console.log(props.date)

    const expensesDates = props.date.map(({date}) => {

    const month = date.toLocaleString("en-UK", { month: "long" });
    const day = date.toLocaleString("en-UK", { day: "2-digit" });
    const year = date.getFullYear();

    });


    console.log(expensesDates)

    const month = expensesDates.toLocaleString("en-UK", { month: "long" });
    const day = expensesDates.toLocaleString("en-UK", { day: "2-digit" });
    const year = expensesDates.getFullYear();

    return(
        <div className='expense-date'>
        <div className='expense-date__month'>{month}</div>
        <div className='expense-date__day'>{day}</div>
        <div className='expense-date__year'>{year}</div>
      </div>
    );
}

export default ExpenseDate;
