import React, { useState } from "react";
import ExpenseItem from "./ExpenseItem";
import Card from "../UI/Card";
import "./Expenses.css";
import ExpensesFilter from "./ExpenseFilter";

const Expenses = (props) => {
  const [storeFilterYear, setStoreFilterYear] = useState("2020");

  const storeFilter = (year) => {
    setStoreFilterYear(year);
    // props.expenseFilter(storeFilter)
  };


  // const filteredList = [];

  // function expenseFilter (expense) {
  //   if(expense.date.includes(storeFilter)){
  //     console.log(expense)
  //   }
  //   props.addFilterHandler(filteredList);
  // }

  let newList = [];
  const filteredList = props.DUMMY_EXPENSES.filter(storeFilter => newList.includes(storeFilter))

  filteredList();


  return (
    <div>
      <Card className="expenses">
        <ExpensesFilter
          selected={storeFilterYear}
          onChangeFilter={storeFilter}
        />
        {/* dynamic expression to render the list based on how many items are there */}
        {/* map takes a function as an argument */}


        {props.expenses.map((expense) => (

          <ExpenseItem
            key={expense.id}
            title={expense.title}
            amount={expense.amount}
            date={expense.date}
          />
        ))}
      </Card>
    </div>
  );
};

export default Expenses;
