import { useState } from "react";

const AddExpenseButton = (props) => {
  const [showForm, setShowForm] = useState(false);
  const toggleButton = () => {
    if (showForm === false) {
      setShowForm(true);
      cancel.remove('hide')
    } else {
      setShowForm(false);
    }
    console.log(showForm);
  };
  let form = props.form;

  let button = <button onClick={toggleButton}>Add New Expense</button>;
  let cancel = <button style={{display: "none"}} onClick={toggleButton}>Cancel</button>;


  if (showForm === true) {
    button = form;
    cancel.style={display:"inherit"};
  }

  return <div>{button}
  {cancel}</div>
};

export default AddExpenseButton;
