import React, { useState } from "react";
import ExpenseItem from "./ExpenseItem";
import Card from "../UI/Card";
import "./Expenses.css";
import ExpensesFilter from "./ExpenseFilter";

const Expenses = (props) => {
  const [storeFilterYear, setStoreFilterYear] = useState("2020");

  const storeFilter = (year) => {
    setStoreFilterYear(year);
  };
  // // function to handle the function for the filter
  // const filterHandler = function (exp) {
  //   if (exp.date.toDateString().includes(storeFilterYear)) {
  //     return true;
  //   }

  // // variable to store the filtered list
  // const newList = props.expenses.filter(filterHandler);
  // // call the function from app.js to send the new list up.
  // props.onFilterByYear(newList);


  const filteredExpenses = props.items.filter( expense => {
    return expense.date.getFullYear().toString() === storeFilterYear;
  });


  return (
    <div>
      <Card className="expenses">
        <ExpensesFilter
          selected={storeFilterYear}
          onChangeFilter={storeFilter}
        />
        {/* dynamic expression to render the list based on how many items are there */}
        {/* map takes a function as an argument */}
        {filteredExpenses.map((expense) => (
          <ExpenseItem
            key={expense.id}
            title={expense.title}
            amount={expense.amount}
            date={expense.date}
          />
        ))}
      </Card>
    </div>
  );
};

export default Expenses;
