import {useState} from 'react';

const AddExpenseButton = () => {
    const [showForm, setShowForm] = useState(false);

    const toggleButton = (props) => {
        let form = props.form
        if(showForm === true){
            setShowForm(false);
            props.form.style.display = "hide"
            console.log(showForm)

        }else{
            setShowForm(true);
            console.log(showForm)

        }
    }
    return(
        <button type="button"onClick={toggleButton}>Add New Expense</button>
    )
}

export default AddExpenseButton;
