import React, { useState } from "react";
import ExpenseItem from "./ExpenseItem";
import Card from "../UI/Card";
import "./Expenses.css";
import ExpensesFilter from "./ExpenseFilter";

const Expenses = (props) => {
  const [storeFilterYear, setStoreFilterYear] = useState("2022");

  const storeFilter = (year) => {
    setStoreFilterYear(year);
  };

  const filteredExpenses = props.expenses.filter( expense => {
    return expense.date.getFullYear().toString() === storeFilterYear;
  })
  return (
    <div>
      <Card className="expenses">
        <ExpensesFilter
          selected={storeFilterYear}
          onChangeFilter={storeFilter}
        />
        {/* dynamic expression to render the list based on how many items are there */}
        {/* map takes a function as an argument */}
        {filteredExpenses === 0 ? (<p>No Expenses Found</p>) : filteredExpenses.map((expense) => (
          <ExpenseItem
            key={expense.id}
            title={expense.title}
            amount={expense.amount}
            date={expense.date}
          />
        ))}

      </Card>
    </div>
  );
};

export default Expenses;
