import ExpenseForm from "./ExpenseForm";


const AddExpenseButton = (props) => {
    const [showForm, setShowForm] = useState(false);

    const toggleButton = () => {
        if(showForm === false) {
            return (button,
            setShowForm(true))
        } else {
            return (button.innerText = "Cancel",
            setShowForm(false), <ExpenseForm onSaveExpenseData={props.saveExpenseDataHandler}/>);
        }
    }

    let button = <button onClick={toggleButton}>Add New Expense</button>;


    return ( 
        {button}
     );
}

export default AddExpenseButton;
