    if (showForm === false) {
      setShowForm(true);
      newFormButton.innerText = 'Cancel';
      console.log(newFormButton)

    } else if (showForm === true) {
      setShowForm(false);

    }
  };

  let newFormButton = <button className="button" onClick={toggleForm}>
  Add New Expense
</button>
