import { useState } from "react";

const AddExpenseButton = (props) => {
   const [showForm, setShowForm] = useState(false);
   const toggleButton = () => {
      if(showForm === false){
          setShowForm(true);
      } else{
          setShowForm(false);
      }
       console.log(showForm)
   }

   let button = <button onClick={toggleButton}>Add New Expense</button>;

   let cancel = <button onClick={toggleButton}>Cancel</button>;

   if(showForm === true) {
       button = props.form;
   } else {
       props.form = button;
   }
   return(
        <div>
            {button}
        </div>
    )
}

export default AddExpenseButton;
